package week1.day1;

public class LearnIfElseAndFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// print all even numbers from 1 to 50
// For is done when we know the no of iterations
		for (int i = 1; i <= 50; i++) {
			if (i % 2 == 0) {
				System.out.println(i);
			}
		}

	}

}
